mapper.update:
UPDATE t_datapoint
SET
    value     = #{value},
    createdAt = #{createdAt},
    sensorId  = #{sensorId}
WHERE id = #{id}

mapper.select:
SELECT *
FROM t_datapoint
WHERE sensorId = #{sensorId}
ORDER BY createdAt ASC
LIMIT #{skipNum}, #{pageSize}

mapper.update:
UPDATE t_datapoint
SET
    value = #{value}
WHERE createdAt = #{createAt} AND sensorId = #{sensorId}
